import Head from 'next/head';
import { nanoid } from 'nanoid';
import { useState, useRef, useEffect } from 'react';

export default function Home() {
  const [formData, setFormData] = useState('');
  const [todos, setTodo] = useState([]);
  const [alert, setAlert] = useState('');

  const inputRef = useRef();

  // Checks to see if any todos have been saved and loads them
  useEffect(() => {
    const data = localStorage.getItem('todos');
    if (data) {
      setTodo(JSON.parse(data));
    }
  }, []);
  // Saves any changes made with todos to locastorage
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  });

  const addTodo = (e) => {
    e.preventDefault();
    if (formData.length === 0) {
      setAlert('You cannot save empty todo!');
      setInterval(() => {
        setAlert('');
      }, 3000);
    }
    const label = inputRef.current;
    setTodo([
      ...todos,
      {
        id: nanoid(6),
        title: formData,
        completed: false,
        edit: false,
      },
    ]);
    setFormData('');
    label.value = '';
    localStorage.setItem('todos', JSON.stringify(todos));
  };
  const removeTodo = (id) => {
    setTodo(todos.filter((todo) => todo.id !== id));
    setAlert('Successfully Removed!');
    setInterval(() => {
      setAlert('');
    }, 3000);
  };

  // Edit whether to do is in edit mode or not to affect how todo gets displayed
  const setTodoEditStatus = (id, title) => {
    setTodo(
      todos.map((todo) => (todo.id === id ? { ...todo, edit: true } : todo))
    );
    setFormData(title);
  };
  // Actual editing of todo
  const editTodo = (id) => {
    console.log(formData.length);
    if (formData.length === 0) {
      setAlert('You cannot save empty todo!');
      setInterval(() => {
        setAlert('');
      }, 3000);
    } else {
      setTodo(
        todos.map((todo) =>
          todo.id === id ? { ...todo, title: formData, edit: false } : todo
        )
      );
    }
  };
  // Allows changing todo to completed or not
  const todoCompletedStatus = (id, completed) => {
    setTodo(
      todos.map((todo) => (todo.id === id ? { ...todo, completed } : todo))
    );
  };
  return (
    <div className='container'>
      <h1 id='header'>To Do List</h1>
      <form action='submit' className='createToDo' onSubmit={(e) => addTodo(e)}>
        <input
          id='todoInput'
          type='text'
          ref={inputRef}
          onChange={(e) => setFormData(e.target.value)}
          placeholder='Enter new To do'
          required
        />
        <button id='createNewButton' type='submit'>
          Create new
        </button>
      </form>
      <p id='alertPlaceholder'>{alert}</p>
      <div id='toDoPlaceholder'>
        <ul>
          {todos.map((todo) => {
            if (todo.edit) {
              return (
                <li key={todo.id}>
                  <input
                    type='text'
                    value={formData}
                    onChange={(e) => setFormData(e.target.value)}
                    id='editTodoInput'
                  />

                  <div>
                    <input
                      type='checkbox'
                      id='editCheckbox'
                      checked={todo.completed}
                      onChange={({ target }) =>
                        todoCompletedStatus(todo.id, target.checked)
                      }
                    />
                    <button
                      onClick={(e) => editTodo(todo.id, formData)}
                      className='liButtons '
                    >
                      Save
                    </button>
                    <button
                      onClick={(e) => removeTodo(todo.id)}
                      className='liButtons '
                    >
                      X
                    </button>
                  </div>
                </li>
              );
            }
            return (
              <li key={todo.id}>
                {todo.title}

                <div>
                  <input
                    className='checkbox '
                    type='checkbox'
                    checked={todo.completed}
                    onChange={({ target }) =>
                      todoCompletedStatus(todo.id, target.checked)
                    }
                  />
                  <button
                    onClick={(e) => setTodoEditStatus(todo.id, todo.title)}
                    className='liButtons '
                  >
                    Edit
                  </button>
                  <button
                    onClick={(e) => removeTodo(todo.id, todo.title)}
                    className='liButtons '
                  >
                    X
                  </button>
                </div>
              </li>
            );
          })}
        </ul>{' '}
        {todos.length > 0 ? (
          <p>Number of todos: {todos.length}</p>
        ) : (
          <p>You have nothing in your todo list</p>
        )}
      </div>
      <style jsx>{`
      @media screen and (max-width: 500px) {
        li {
          font-size: 1rem;
          padding: 1px;
          display: grid;
          grid-template-columns: 20%  auto;
          grid-gap: 1rem

        }
        ul {
          list-style: none;
          padding: 0px;
          width: 100%;
          display: grid;
          grid-template-columns: auto
          grid-gap: 10px;
          color: yellow
        }
        p{
          font-size: 1rem;
            color: white
        }
      }
        #todoInput {
          padding: 1.5rem;
          display: block;
          margin: 0 auto;
          width: 60%;
          margin-bottom: 1rem;
        }
        #toDoPlaceholder {
          padding: 5rem;
        }
        #editTodoInput {
          padding: 1rem;
          width: 40%;
          margin-left: 30%;
        }
        .checkbox {
          width: 2rem;
          height: 2rem;
          vertical-align: middle;
        }

        #editCheckbox {
          width: 2rem;
          height: 2rem;
          vertical-align: middle;
        }
        #createNewButton {
          background-color: white;
          padding: 1rem;
        }
        .liButtons {
          background-color: white;
          padding: 0.5rem;
        }
      `}</style>

      <style jsx global>
        {`
          body {
            background-color: #db6581;
            margin: 0;
            padding: 0;
            font-size: 18px;
            font-weight: 400;
            line-height: 1.8;
            color: #333;
            font-family: sans-serif;
            height: 100%;
            width: 100%;
            position: absolute;
          }
          #header {
            color: #73b0b4;
            transform: scale(1.5);
            padding: 2rem;
          }

          .container {
            display: block;
            text-align: center;
          }
          p {
            font-size: 2rem;
            color: white
          }
          li {
            font-size: 2rem;
            padding: 2px;
            display: grid;
            grid-template-columns: 60%  auto;
            grid-gap: 1rem
          }
          ul {
            list-style: none;
            padding: 0px;
            width: 100%;
            display: grid;
            grid-template-columns: auto
            grid-gap: 10px;
          }
        `}
      </style>
    </div>
  );
}
