import Head from 'next/head';
import { nanoid } from 'nanoid';
import { useState, useRef, useEffect } from 'react';

export default function Home() {
  const [formData, setFormData] = useState('');
  const [todos, setTodo] = useState([]);

  const inputRef = useRef();

  useEffect(() => {
    todos = JSON.parse(localStorage.getItem('todos'));
  });

  const addTodo = (e) => {
    e.preventDefault();
    const label = inputRef.current;
    setTodo([
      ...todos,
      {
        id: nanoid(6),
        title: formData,
        completed: false,
      },
    ]);
    label.value = '';
    localStorage.setItem('todos', JSON.stringify(todos));
  };
  const removeTodo = (id) => {
    setTodo(todos.filter((todo) => todo.id !== id));
  };
  const todoStatus = (id, completed) => {
    setTodo(
      todos.map((todo) => (todo.id === id ? { ...todo, completed } : todo))
    );
  };
  return (
    <div className='container'>
      <h1>To Do List</h1>
      <form action='submit' className='createToDo' onSubmit={(e) => addTodo(e)}>
        <input
          type='text'
          ref={inputRef}
          onChange={(e) => setFormData(e.target.value)}
          placeholder='Enter new To do'
        />
        <button type='submit'>Create new</button>
      </form>
      <div className='toDoPlaceholder'>
        <ul>
          {todos.map((todo) => {
            return (
              <li key={todo.id}>
                {todo.title}
                <input
                  type='checkbox'
                  checked={todo.completed}
                  onChange={({ target }) => todoStatus(todo.id, target.checked)}
                />
                <span onClick={(e) => removeTodo(todo.id)}>X</span>
              </li>
            );
          })}
        </ul>{' '}
      </div>
      <style jsx>{``}</style>

      <style jsx global>{``}</style>
    </div>
  );
}
