import Head from 'next/head';
import { nanoid } from 'nanoid';
import { useState, useRef, useEffect } from 'react';

export default function Home() {
  const [formData, setFormData] = useState('');
  const [todos, setTodo] = useState([]);

  const inputRef = useRef();

  // Checks to see if any todos have been saved and loads them
  useEffect(() => {
    const data = localStorage.getItem('todos');
    if (data) {
      setTodo(JSON.parse(data));
    }
  }, []);
  // Saves any changes made with todos to locastorage
  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  });

  const addTodo = (e) => {
    e.preventDefault();
    const label = inputRef.current;
    setTodo([
      ...todos,
      {
        id: nanoid(6),
        title: formData,
        completed: false,
        edit: false,
      },
    ]);
    label.value = '';
    localStorage.setItem('todos', JSON.stringify(todos));
  };
  const removeTodo = (id) => {
    setTodo(todos.filter((todo) => todo.id !== id));
  };

  // Edit whether to do is in edit mode or not to affect how todo gets displayed
  const setTodoEditStatus = (id, edit) => {
    setTodo(
      todos.map((todo) => (todo.id === id ? { ...todo, edit: true } : todo))
    );
    console.log(todos);
  };
  // Actual editing of todo
  const editTodo = (id, newText) => {
    setTodo(
      todos.map((todo) =>
        todo.id === id ? { ...todo, title: formData, edit: false } : todo
      )
    );
  };
  // Allows changing todo to completed or not
  const todoCompletedStatus = (id, completed) => {
    setTodo(
      todos.map((todo) => (todo.id === id ? { ...todo, completed } : todo))
    );
  };
  return (
    <div id='fullPage'>
      <div className='container'>
        <h1 id='header'>To Do List</h1>
        <form
          action='submit'
          className='createToDo'
          onSubmit={(e) => addTodo(e)}
        >
          <input
            type='text'
            ref={inputRef}
            onChange={(e) => setFormData(e.target.value)}
            placeholder='Enter new To do'
            required
          />
          <button type='submit'>Create new</button>
        </form>
        <div className='toDoPlaceholder'>
          <ul>
            {todos.map((todo) => {
              if (todo.edit) {
                return (
                  <li key={todo.id}>
                    <input
                      type='text'
                      onChange={(e) => setFormData(e.target.value)}
                    />
                    <input
                      type='checkbox'
                      checked={todo.completed}
                      onChange={({ target }) =>
                        todoStatus(todo.id, target.checked)
                      }
                    />
                    <button onClick={(e) => editTodo(todo.id, formData)}>
                      Save
                    </button>
                    <button onClick={(e) => removeTodo(todo.id)}>X</button>
                  </li>
                );
              }
              return (
                <li key={todo.id}>
                  {todo.title}
                  <input
                    type='checkbox'
                    checked={todo.completed}
                    onChange={({ target }) =>
                      todoCompletedStatus(todo.id, target.checked)
                    }
                  />
                  <button onClick={(e) => setTodoEditStatus(todo.id)}>
                    Edit
                  </button>
                  <button onClick={(e) => removeTodo(todo.id)}>X</button>
                </li>
              );
            })}
          </ul>{' '}
          {todos.length > 0 ? (
            <p>Number of todos: {todos.length}</p>
          ) : (
            <p>You have nothing in todo list</p>
          )}
        </div>
        <style jsx>{`
          #header {
            color: #73b0b4;
          }
          #fullPage {
            background-color: #db6581;
            width: 100%;
            height: 10px;
          }
          .container {
            margin: 0 auto;
            text-align: center;
          }
        `}</style>

        <style jsx global>{``}</style>
      </div>
    </div>
  );
}
